import React, { useState } from 'react';

function App() {
  const [board, setBoard] = useState(Array(9).fill(null));
  const [turn, setTurn] = useState('X');

  const handleClick = async (i) => {
    if (board[i] || turn !== 'X') return;

    const newBoard = [...board];
    newBoard[i] = 'X';
    setBoard(newBoard);
    setTurn('O');

    const state = newBoard.map(cell => cell || '-').join('');

    const res = await fetch('http://127.0.0.1:5000/api/next-move', {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({ state })
});


    const data = await res.json();
    if (data.move !== undefined) {
      newBoard[data.move] = 'O';
      setBoard(newBoard);
      setTurn('X');
    }
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 60px)', gap: '5px' }}>
      {board.map((cell, i) => (
        <button key={i} onClick={() => handleClick(i)} style={{ height: '60px', fontSize: '24px' }}>
          {cell}
        </button>
      ))}
    </div>
  );
}

export default App;
