import React, { useState } from 'react'
import axios from 'axios'

export default function TicTacToe() {
  const [board, setBoard] = useState(Array(9).fill(''))
  const [winner, setWinner] = useState(null)

  const handleClick = async (idx) => {
    if (board[idx] || winner) return
    const newBoard = [...board]
    newBoard[idx] = 'O'
    setBoard(newBoard)
    // send to backend for agent move
    const res = await axios.post('/api/move', { board: newBoard })
    setBoard(res.data.board)
    setWinner(res.data.winner)
  }

  return (
    <div className="p-4 bg-white rounded shadow">
      <div className="grid grid-cols-3 gap-2">
        {board.map((cell, i) => (
          <button
            key={i}
            onClick={() => handleClick(i)}
            className="w-16 h-16 text-2xl bg-gray-200 rounded"
          >{cell}</button>
        ))}
      </div>
      {winner && <div className="mt-4 text-center text-lg">{winner === 'Draw' ? "It's a draw!" : `${winner} wins!`}</div>}
      <button onClick={() => {setBoard(Array(9).fill('')); setWinner(null)}} className="mt-4 block mx-auto px-4 py-2 bg-blue-500 text-white rounded">Reset</button>
    </div>
  )
}
